<?php

$exports["sum"] = function ($a, $b) {
    return $a + $b;
};

$exports["fib"] = function ($count) {
    
    $num = 0;
    $last = 0;
    $next = 0;

    for ($i = 0; $i < $count -1; $i++) {
        $num = $last + $next;
        $last = $next;
        $next = $num;
        if ($last === 0) {
            $last = 1;
        }
    }

    return $num;
};